def hasCommitPipeline = false;
project projName, {
    getPipelines().each { 
		if(it.name == commitPipeName) {
			hasCommitPipeline = true;
		}
	}

	if(!hasCommitPipeline) {
		pipeline commitPipeName, {
			pipe.stages.eachWithIndex { st, index ->
				stage st,{

					if (index == 0) { // QA Stage
						colorCode = '#2ca02c' 
						apps.each { app ->
							task 'Deploy to QA', {
								taskType = 'PROCESS'
								taskProcessType = 'APPLICATION'
								subapplication = app
								subproject = projName
								subprocess = 'Deploy'
								environmentName = envNames[0]
								environmentProjectName = projName
							}
						}

						task 'Integration Test', {
							taskType = 'MANUAL'
							approver = approvers
							instruction = 'Verify that business requirements are met'
							notificationTemplate = 'ec_default_pipeline_manual_task_notification_template'
							requiredApprovalsCount = '1'
						}

						task 'Load/Stress Test', {
							description = ''
							taskType = 'PROCEDURE'
							subproject = projName
							subprocedure = 'LoadTest'
							enabled = '0'
						}

					}  else if (index == 1) { //Prod Stage
						colorCode = '#00adee'
						task 'Deploy to Prod', {
							taskType = 'DEPLOYER'
							deployerRunType = 'parallel'
						}
						apps.each { app ->
							task 'Deploy to Prod', {
								taskType = 'PROCESS'
								taskProcessType = 'APPLICATION'
								subapplication = app
								subproject = projName
								subprocess = 'Deploy'
								environmentName = envNames[1]
								environmentProjectName = projName
							}
						}

						task 'Entry gate approval', {
							taskType = 'APPROVAL'
							approver = ['admin']
							gateType = 'PRE'
							notificationTemplate = 'ec_default_pipeline_notification_template'
						}
					}
					else { //Audit Stage
						colorCode = '#ff7f0e'
						task 'Generate Audit Report', {
							description = ''
							taskType = 'PLUGIN'
							subpluginKey = 'EC-AuditReports'
							subprocedure = 'Audit Reports'
						}
					}
				}
			}
		} 	
	}

}

